import React, { useEffect, useState } from "react";
import SearchBar from "./SearchBar";

const Wishlist = () => {
  const [likedRecipes, setLikedRecipes] = useState([]);
  const [filteredRecipes, setFilteredRecipes] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [showSearchBar, setShowSearchBar] = useState(true);
  const [showGoBackButton, setShowGoBackButton] = useState(false);

  useEffect(() => {
    fetchLikedRecipes();
  }, []);

  useEffect(() => {
    filterRecipes();
  }, [searchQuery, likedRecipes]);

  const fetchLikedRecipes = async () => {
    try {
      const response = await fetch("https://test-git-main-joelsajiputhenpurakkals-projects.vercel.app/api/recipes/wishlist", {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      });

      if (!response.ok) {
        throw new Error("Failed to fetch liked recipes");
      }

      const data = await response.json();
      setLikedRecipes(data);
      setFilteredRecipes(data); // Initialize filtered recipes with all liked recipes
    } catch (error) {
      console.error("Error fetching liked recipes:", error);
    }
  };

  const filterRecipes = () => {
    if (searchQuery.trim() === "") {
      setFilteredRecipes(likedRecipes);
    } else {
      setFilteredRecipes(
        likedRecipes.filter(recipe =>
          recipe.label.toLowerCase().includes(searchQuery.toLowerCase())
        )
      );
    }
  };

  const handleSearch = (query) => {
    setSearchQuery(query);
  };

  const handleDeleteRecipe = async (id) => {
    try {
      const response = await fetch(`https://test-git-main-joelsajiputhenpurakkals-projects.vercel.app/api/recipes/wishlist/${id}`, {
        method: "DELETE",
        headers: {
          "Content-Type": "application/json",
        },
      });

      if (!response.ok) {
        throw new Error("Failed to delete recipe");
      }

      // Update the UI by removing the deleted recipe
      setLikedRecipes((prevRecipes) => prevRecipes.filter(recipe => recipe._id !== id));
    } catch (error) {
      console.error("Error deleting recipe:", error);
    }
  };

  const handleViewRecipe = (recipe) => {
    window.open(recipe.url, "_blank");
  };

  const handleGoBack = () => {
    setShowSearchBar(true);
    setShowGoBackButton(false);
    // Additional logic to navigate back if necessary
  };

  return (
    <div className="wishlist">
      <h2>Your Wishlist</h2>
      {showSearchBar && <SearchBar onSearch={handleSearch} />} {/* Conditionally render SearchBar */}
      {showGoBackButton && <button onClick={handleGoBack}>Go Back</button>} {/* Conditionally render Go Back button */}
      {filteredRecipes.length === 0 ? (
        <p>No recipes found.</p>
      ) : (
        <div className="recipe-list">
          {filteredRecipes.map((recipe, index) => (
            <div key={index} className="recipe-card">
              <img src={recipe.image} alt={recipe.label} />
              <h2>{recipe.label}</h2>
              <p>Source: {recipe.source}</p>
              <div className="button-group">
                <button onClick={() => handleViewRecipe(recipe)}>View Recipe</button>
                <button onClick={() => handleDeleteRecipe(recipe._id)}>Delete</button>
              </div>
            </div>
          ))}
        </div>
      )}
      <button onClick={() => {
        setShowSearchBar(false);
        setShowGoBackButton(true);
      }}>
        Wishlist
      </button>
    </div>
  );
};

export default Wishlist;
