import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const Wishlist = () => {
  const [likedRecipes, setLikedRecipes] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetchLikedRecipes();
  }, []);

  const fetchLikedRecipes = async () => {
    try {
      const response = await fetch("https://test-xi-steel-82.vercel.app/api/recipes/wishlist", {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      });

      if (!response.ok) {
        throw new Error("Failed to fetch liked recipes");
      }

      const data = await response.json();
      setLikedRecipes(data); // Set the fetched liked recipes to state
    } catch (error) {
      console.error("Error fetching liked recipes:", error);
    }
  };

  const handleViewRecipe = (recipe) => {
    navigate("/recipe-summary", { state: { recipe: recipe } });
  };

  return (
    <div className="wishlist">
      <h2>Your Wishlist</h2>
      {likedRecipes.length === 0 ? (
        <p>No liked recipes found.</p>
      ) : (
        <div className="recipe-list">
          {likedRecipes.map((recipe, index) => (
            <div key={index} className="recipe-card">
              <img src={recipe.image} alt={recipe.label} />
              <h2>{recipe.label}</h2>
              <p>Source: {recipe.source}</p>
              <button onClick={() => handleViewRecipe(recipe)}>View Recipe</button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Wishlist;
