import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import SearchBar from "./components/SearchBar";
import RecipeList from "./components/RecipeList";
import RecipeSummary from "./components/RecipeSummary";
import Wishlist from "./components/ishlist";
import "./App.css";

const App = () => {
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [wishlist, setWishlist] = useState([]);

  const fetchRecipes = async (query) => {
    setLoading(true);
    const API_ID = "5d9da354";
    const API_KEY = "5fc681fbb7ec51f9ca2d635101090ab4";
    const response = await fetch(
      `https://api.edamam.com/search?q=${query}&app_id=${API_ID}&app_key=${API_KEY}`
    );
    const data = await response.json();
    setRecipes(data.hits);
    setLoading(false);
  };

  const addToWishlist = (recipe) => {
    setWishlist((prevWishlist) => [...prevWishlist, recipe]);
  };

  return (
    <Router>
      <div className="App">
        <h1>Recipe Finder</h1>
        <SearchBar onSearch={fetchRecipes} />
        <nav>
          <Link to="/">Home</Link> | <Link to="/wishlist">Wishlist</Link>
        </nav>
        <Routes>
          <Route
            path="/"
            element={
              loading ? (
                <p className="loading">Loading...</p>
              ) : (
                <RecipeList recipes={recipes} addToWishlist={addToWishlist} />
              )
            }
          />
          <Route path="/recipe-summary" element={<RecipeSummary />} />
          <Route path="/wishlist" element={<Wishlist wishlist={wishlist} />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
